<?php if (!defined('THINK_PATH')) exit();?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><meta name="viewport" content="width=device-width, initial-scale=1.0" /><title>Forms | Amanda Admin Template</title><?php  common_css(); common_js(); ?><script type="text/javascript">	$(function(){		$('input').click(function(){			var level = $(this).attr('level');			if(level==1){				var str = '_';				var inputs = $('input[value*='+str+']');				$(this).attr('checked')?inputs.attr('checked',true):inputs.removeAttr('checked');			}else if(level==2){				var id=$(this).attr('id');				var inputs = $('input[pid='+id+']');				$(this).attr('checked')?inputs.attr('checked',true):inputs.removeAttr('checked');			}else if(level==3){				if($(this).attr('checked')){					var pid=$(this).attr('pid');					$('input[id='+pid+']').attr('checked',true);										var ppid=$('input[id='+pid+']').attr('pid');					$('input[id='+ppid+']').attr('checked',true);				}								$(this).attr('checked')?inputs.attr('checked',true):inputs.removeAttr('checked');			}		})	});</script></head><body class=""><div class="">    <div class="pageheader">            <h1 class="pagetitle">                         <a href="javascript:void(0)" class="btn btn4 btn_orange btn_bulb"></a>                            分配权限</h1>            <span class="pagedesc"></span>           <ul class="toolbar">                   		<li><a href="#" class="btn btn_blue   btn_archive"><span>您正在为用户组：<?php echo ($name); ?>分配权限</span></a></li>                    </ul>        </div>                <div id="contentwrapper" class="contentwrapper">        	        	<form class="stdform stdform2" method="post" action="<?php echo U('Rbac/accessHandle');?>">                    	<input type="hidden" name="rid" value="<?php echo ($rid); ?>" />                    	<?php if(is_array($nodeList)): $k = 0; $__LIST__ = $nodeList;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$vo): $mod = ($k % 2 );++$k;?><p style="text-indent: <?php echo ($vo['level']*20); ?>px; <?php if($vo["level"] == 3): ?>float:left;<?php else: ?>clear:both;<?php endif; ?>" >                           	<input type="checkbox" id="<?php echo ($vo["id"]); ?>" name="access[]" value="<?php echo ($vo["id"]); ?>_<?php echo ($vo["level"]); ?>" pid="<?php echo ($vo["pid"]); ?>" level="<?php echo ($vo["level"]); ?>" <?php if($vo["access"] == 1): ?>checked="checked"<?php endif; ?>/>                       		<?php if($vo["level"] == 1): ?>[项目]<?php elseif($vo["level"] == 2): ?>[模块]<?php else: ?>[操作]<?php endif; ?>                       		<span name="name" value="<?php echo ($vo["id"]); ?>" ><?php echo ($vo["title"]); ?></span>                        </p><?php endforeach; endif; else: echo "" ;endif; ?>                                                <p class="stdformbutton">                        	<button class="submit radius2">分配权限</button>                            <input type="reset" class="reset radius2" value="重置" />                        </p>                    </form>        </div><!--contentwrapper-->        	</div></body></html>